import { X } from "lucide-react";
import { useEffect } from "react";

interface ArtworkModalProps {
  isOpen: boolean;
  onClose: () => void;
  title: string;
  artist: string;
  imageUrl: string;
  year: string;
  description: string;
}

export const ArtworkModal = ({
  isOpen,
  onClose,
  title,
  artist,
  imageUrl,
  year,
  description,
}: ArtworkModalProps) => {
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "unset";
    }
    return () => {
      document.body.style.overflow = "unset";
    };
  }, [isOpen]);

  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 z-50 flex items-center justify-center bg-background/80 backdrop-blur-sm animate-scale-in"
      onClick={onClose}
    >
      <div
        className="relative max-w-5xl w-full mx-4 bg-card rounded-2xl shadow-[var(--shadow-card-hover)] overflow-hidden"
        onClick={(e) => e.stopPropagation()}
      >
        <button
          onClick={onClose}
          className="absolute top-4 right-4 z-10 p-2 rounded-full bg-background/80 backdrop-blur-sm hover:bg-background transition-colors"
        >
          <X className="w-5 h-5" />
